import { MockClient } from "../../http/MockClient";
import { BulkRequestClient } from "../BulkRequestClient";
describe("BulkRequestClient", function () {
    var _a;
    var mockClient;
    var bulkRequestClient;
    var APP_ID = 1;
    var RECORD_ID = 2;
    var fieldCode = "Customer";
    var record = (_a = {},
        _a[fieldCode] = {
            value: "ABC Corporation"
        },
        _a);
    beforeEach(function () {
        mockClient = new MockClient();
        bulkRequestClient = new BulkRequestClient(mockClient);
    });
    describe("send", function () {
        var params = {
            requests: [
                {
                    method: "POST",
                    api: "/k/v1/record.json",
                    payload: {
                        app: APP_ID,
                        record: record
                    }
                },
                {
                    method: "DELETE",
                    api: "/k/v1/records.json",
                    payload: {
                        app: APP_ID,
                        ids: [RECORD_ID]
                    }
                }
            ]
        };
        beforeEach(function () {
            bulkRequestClient.send(params);
        });
        it("should pass the path to the http client", function () {
            expect(mockClient.getLogs()[0].path).toBe("/k/v1/bulkRequest.json");
        });
        it("should send a post request", function () {
            expect(mockClient.getLogs()[0].method).toBe("post");
        });
        it("should pass requests to the http client", function () {
            expect(mockClient.getLogs()[0].params).toEqual(params);
        });
    });
});
describe("BulkRequestClient with guestSpaceId", function () {
    it("should pass the path to the http client", function () {
        var APP_ID = 1;
        var GUEST_SPACE_ID = 2;
        var mockClient = new MockClient();
        var bulkRequestClient = new BulkRequestClient(mockClient, GUEST_SPACE_ID);
        var params = {
            requests: [
                {
                    method: "POST",
                    api: "/k/v1/record.json",
                    payload: {
                        app: APP_ID,
                        record: {
                            Customer: {
                                value: "ABC Corporation"
                            }
                        }
                    }
                }
            ]
        };
        bulkRequestClient.send(params);
        expect(mockClient.getLogs()[0].path).toBe("/k/guest/" + GUEST_SPACE_ID + "/v1/bulkRequest.json");
    });
});
